import React, { Component } from "react";
import Jumbotron from "../components/Jumbotron";
import Card from "../components/Card";
import API from "../utils/API";

const moviesTheme = ["There will be blood","The Departed", "Memento", "Gladiator", "City Of God",
                          "Pan's Labrynth", "Up", "Donnie Darko", "28 Days Late",
                          "High Fidelity", "Anchorman", "A.I.: Artificial Intelligence", "Pulp Fiction", "Billy Madison",
                          "The Usual Suspects", "Dumb and Dumber", "Goodfellas", "Hoop Dreams", "Groundhog Day",
                          "The Matrix", "Harry and the Hendersons", "Toy Story", "Princess Mononoke" ];


class GameBoard extends Component {
  state = {
    picked: [],
    free: [],
    score: 0,
    topScore: 0
  };

  componentDidMount() {
    this.loadData("The Usual Suspects");
    console.log(this.state.free);
  }

  camelize(str) {
    return str.replace(/(?:^\w|[A-Z]|\b\w)/g, function(word, index) {
      return index == 0 ? word.toLowerCase() : word.toUpperCase();
    }).replace(/\s+/g, '');
  }

  loadData = (query) => {
    console.log("Searching ...")
    API.search(query)
      .then(res =>
        this.setState( prevState => ({
          free: [...prevState.free, {id: this.camelize(res.data.Title),
                   thumbnail: res.data.Poster}]
        })
      ))
      .catch(err => console.log(err));

      console.log("FREE :", this.state.free);
  };

  handleButtonClick() {
    console.log("click");
  }

  render() {
    return (
      <>
      <Jumbotron backgroundImage = "http://www.profilerehab.com/facebook_covers/gaming/cool_mario_cover_3.jpg">
      <h1 className="title">Clicky Game!</h1>
      <p className="description">Click on a card to earn points, but don't click on the same card twice!</p>
      </Jumbotron>
      <div className="container">
        Image: {this.state.image}
      <Card id="111"
            thumbnail = "https://mariokart8.nintendo.com/assets/img/top/char_mario.png"
      />
      </div>
      </>
    );
}
}

export default GameBoard;
