import React, { Component } from "react";
import Jumbotron from "../components/Jumbotron";
import Card from "../components/Card";
import API from "../utils/API";

const moviesTheme = [{id: "myNeighborTotoro", thumbnail: "https://m.media-amazon.com/images/M/MV5BNTdiOTQ0YmUtOGE3YS00NDg5LWI3YTEtNDAxZmE0MzRmZWM5L2ltYWdlXkEyXkFqcGdeQXVyNTAyODkwOQ@@._V1_SX300.jpg"},
                     {id: "princessMononoke", thumbnail: "https://m.media-amazon.com/images/M/MV5BMTVlNWM4NTAtNDQxYi00YWU5LWIwM2MtZmVjYWFmODZiODE5XkEyXkFqcGdeQXVyNjU0OTQ0OTY@._V1_SX300.jpg"},
                     {id: "castleInTheSky", thumbnail: "https://m.media-amazon.com/images/M/MV5BNTg0NmI1ZGQtZTUxNC00NTgxLThjMDUtZmRlYmEzM2MwOWYwXkEyXkFqcGdeQXVyMzM4MjM0Nzg@._V1_SX300.jpg"},
                     {id: "graveOfTheFireflies", thumbnail: "https://m.media-amazon.com/images/M/MV5BZmY2NjUzNDQtNTgxNC00M2Q4LTljOWQtMjNjNDBjNWUxNmJlXkEyXkFqcGdeQXVyNTA4NzY1MzY@._V1_SX300.jpg"},
                     {id: "spiritedAway", thumbnail: "https://m.media-amazon.com/images/M/MV5BOGJjNzZmMmUtMjljNC00ZjU5LWJiODQtZmEzZTU0MjBlNzgxL2ltYWdlXkEyXkFqcGdeQXVyNTAyODkwOQ@@._V1_SX300.jpg"},
                     {id: "theCatReturns", thumbnail: "https://m.media-amazon.com/images/M/MV5BMTQ5ODMyNTgzOV5BMl5BanBnXkFtZTcwNDM4ODAyNw@@._V1_SX300.jpg"},
                     {id: "kikisDeliveryService", thumbnail: "https://m.media-amazon.com/images/M/MV5BOTc0ODM1Njk1NF5BMl5BanBnXkFtZTcwMDI5OTEyNw@@._V1_SX300.jpg"},
                     {id: "ponyo", thumbnail: "https://m.media-amazon.com/images/M/MV5BMjA5NzkxNTg2MF5BMl5BanBnXkFtZTcwMTA3MjU1Mg@@._V1_SX300.jpg"},
                     {id: "", thumbnail: ""},
                     {id: "", thumbnail: ""},
                     {id: "", thumbnail: ""},
                     {id: "", thumbnail: ""}
                    ];


class GameBoard extends Component {
  state = {
    cards: [],
    free: [],
    score: 0,
    topScore: 0
  };

  componentDidMount() {
      this.loadData("howl")
  }

  camelize(str) {
    return str.replace(/(?:^\w|[A-Z]|\b\w)/g, function(word, index) {
      return index === 0 ? word.toLowerCase() : word.toUpperCase();
    }).replace(/\s+/g, '');
  }

  loadData = (query) => {
    let myTempArray = this.state.free;
    let myData;
    
    console.log("Searching ...")
    API.search(query)
      .then(res =>
        console.log(res.data)
      )
      .then(res => {
        // myTempArray.push(myData)
      })
      .then( res=> {
        // this.setState({ free: myTempArray })
      })
      .catch(err => console.log(err));

      console.log("Free Array: ", this.state.free)
  };

  handleButtonClick() {
    console.log("click");
  }

  render() {
    return (
      <>
      <Jumbotron backgroundImage = "http://www.profilerehab.com/facebook_covers/gaming/cool_mario_cover_3.jpg">
      <h1 className="title">Clicky Game!</h1>
      <p className="description">Click on a card to earn points, but don't click on the same card twice!</p>
      </Jumbotron>
      <div className="container">
        Image: {this.state.free[0]}
      <Card id="111"
            thumbnail = "https://mariokart8.nintendo.com/assets/img/top/char_mario.png"
      />
      </div>
      </>
    );
}
}

export default GameBoard;
